installInfo.installs.some(function(install) {
  if (install.error) {
    name = install.name;
    error = "addonError" + install.error;
    return true;
  }
  if (install.addon.blocklistState ==
      Components.interfaces.nsIBlocklistService.STATE_BLOCKED) {
    name = install.name;
    error = "addonErrorBlocklisted";
  }
  return false;
});

