  let firstTabNode = this.tabContainer.firstChild;
  firstTabInfo.mode.tabs.push(firstTabInfo);
  this.tabInfo[0] = this.currentTabInfo = firstTabInfo;
  this.setTabTitle(firstTabInfo);
  if (this.tabMonitors.length)
